// Import Libraries
import { Component, OnInit } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
// Import Services
import { ProfessorService } from '../../services/professor.service';
import { ProvaService } from '../../services/prova.service';
import { CursoService } from '../../services/curso.service';
// Import Models
import { Professor } from '../../domain/escola_db/professor';
import { Curso } from '../../domain/escola_db/curso';
import { Prova } from '../../domain/escola_db/prova';

// START - USED SERVICES
/**
* professorService.create
*	@description CRUD ACTION create
*
* professorService.update
*	@description CRUD ACTION update
*	@param ObjectId id Id
*
* professorService.get
*	@description CRUD ACTION get
*	@param ObjectId id Id resource
*
* provaService.findBy_professor
*	@description CRUD ACTION findBy_professor
*	@param Objectid key Id of model to search for
*
* cursoService.list
*	@description CRUD ACTION list
*
*/
// END - USED SERVICES

/**
 * This component allows to edit a Professor
 */
@Component({
    selector: 'app-professor-edit',
    templateUrl: 'professor-edit.component.html',
    styleUrls: ['professor-edit.component.css']
})
export class ProfessorEditComponent implements OnInit {
    item: Professor;
    list_cursos: Curso[];
    externalProva__professor: Prova[];
    model: Professor;
    formValid: Boolean;

    constructor(
    private professorService: ProfessorService,
    private provaService: ProvaService,
    private cursoService: CursoService,
    private route: ActivatedRoute,
    private location: Location) {
        // Init item
        this.item = new Professor();
        this.externalProva__professor = [];
    }

    /**
     * Init
     */
    ngOnInit() {
        this.route.params.subscribe(param => {
            const id: string = param['id'];
            if (id !== 'new') {
                this.professorService.get(id).subscribe(item => this.item = item);
                this.provaService.findBy_professor(id).subscribe(list => this.externalProva__professor = list);
            }
            // Get relations
            this.cursoService.list().subscribe(list => this.list_cursos = list);
        });
    }

    /**
     * Check if an Curso is in  _cursos
     *
     * @param {string} id Id of Curso to search
     * @returns {boolean} True if it is found
     */
    containCurso(id: string): boolean {
        if (!this.item._cursos) return false;
        return this.item._cursos.indexOf(id) !== -1;
    }

    /**
     * Add Curso from Professor
     *
     * @param {string} id Id of Curso to add in this.item._cursos array
     */
    addCurso(id: string) {
        if (!this.item._cursos)
            this.item._cursos = [];
        this.item._cursos.push(id);
    }

    /**
     * Remove an Curso from a Professor
     *
     * @param {number} index Index of Curso in this.item._cursos array
     */
    removeCurso(index: number) {
        this.item._cursos.splice(index, 1);
    }

    /**
     * Save Professor
     *
     * @param {boolean} formValid Form validity check
     * @param Professor item Professor to save
     */
    save(formValid: boolean, item: Professor): void {
        this.formValid = formValid;
        if (formValid) {
            if (item._id) {
                this.professorService.update(item).subscribe(data => this.goBack());
            } else {
                this.professorService.create(item).subscribe(data => this.goBack());
            } 
        }
    }

    /**
     * Go Back
     */
    goBack(): void {
        this.location.back();
    }


}



